  <div class="container">
    <header>
      <h1>🧠 Neurosynth Terms</h1>
      <p class="subtitle">Browse and search neuroscience research terms</p>
    </header>

    <div class="controls">
      <input 
        type="text" 
        id="searchBox" 
        class="search-box" 
        placeholder="🔍 Search terms..."
      />
      <div class="sort-options">
        <span style="color: #666; font-size: 14px;">Sort:</span>
        <button class="sort-btn active" data-sort="az">A-Z</button>
        <button class="sort-btn" data-sort="za">Z-A</button>
        <button class="sort-btn" data-sort="length">Length</button>
      </div>
      <button id="refreshBtn" class="btn">🔄 Refresh</button>
    </div>

    <div class="stats" id="stats" style="display: none;">
      <div class="stat-item">
        <div class="stat-number" id="totalCount">0</div>
        <div class="stat-label">Total Terms</div>
      </div>
      <div class="stat-item">
        <div class="stat-number" id="displayCount">0</div>
        <div class="stat-label">Displayed</div>
      </div>
    </div>

    <div class="terms-container">
      <div id="output" class="loading">Loading terms</div>
    </div>
  </div>

  <script>
    let allTerms = [];
    let currentSort = 'az';

    async function fetchTerms() {
      const output = document.getElementById('output');
      output.innerHTML = '<div class="loading">Loading terms</div>';

      try {
        const resp = await fetch('https://mil.psy.ntu.edu.tw:5000/terms');

        if (!resp.ok) {
          throw new Error(`HTTP ${resp.status}: ${resp.statusText}`);
        }

        const data = await resp.json();

        if (data && data.terms && Array.isArray(data.terms)) {
          allTerms = data.terms;
          document.getElementById('totalCount').textContent = allTerms.length;
          document.getElementById('stats').style.display = 'flex';
          displayTerms(allTerms);
        } else {
          throw new Error('Invalid data format');
        }
      } catch (err) {
        output.innerHTML = `
          <div class="error">
            <strong>Error loading terms:</strong><br>
            ${err.message}<br><br>
            <small>Please make sure the server is running and CORS is enabled.</small>
          </div>
        `;
      }
    }

    function displayTerms(terms) {
      const output = document.getElementById('output');
      document.getElementById('displayCount').textContent = terms.length;

      if (terms.length === 0) {
        output.innerHTML = '<div class="no-results">No terms found matching your search.</div>';
        return;
      }

      const grid = document.createElement('div');
      grid.className = 'terms-grid';

      terms.forEach(term => {
        const card = document.createElement('div');
        card.className = 'term-card';
        card.textContent = term;
        card.title = `Click to copy: ${term}`;
        card.addEventListener('click', () => {
          navigator.clipboard.writeText(term).then(() => {
            const originalText = card.textContent;
            card.textContent = '✓ Copied!';
            setTimeout(() => {
              card.textContent = originalText;
            }, 1000);
          });
        });
        grid.appendChild(card);
      });

      output.innerHTML = '';
      output.appendChild(grid);
    }

    function sortTerms(terms, sortType) {
      const sorted = [...terms];
      switch(sortType) {
        case 'az':
          return sorted.sort((a, b) => a.localeCompare(b));
        case 'za':
          return sorted.sort((a, b) => b.localeCompare(a));
        case 'length':
          return sorted.sort((a, b) => a.length - b.length || a.localeCompare(b));
        default:
          return sorted;
      }
    }

    function filterAndDisplay() {
      const searchTerm = document.getElementById('searchBox').value.toLowerCase();
      const filtered = allTerms.filter(term => term.toLowerCase().includes(searchTerm));
      const sorted = sortTerms(filtered, currentSort);
      displayTerms(sorted);
    }

    // Event listeners
    document.getElementById('searchBox').addEventListener('input', filterAndDisplay);

    document.getElementById('refreshBtn').addEventListener('click', fetchTerms);

    document.querySelectorAll('.sort-btn').forEach(btn => {
      btn.addEventListener('click', (e) => {
        document.querySelectorAll('.sort-btn').forEach(b => b.classList.remove('active'));
        e.target.classList.add('active');
        currentSort = e.target.dataset.sort;
        filterAndDisplay();
      });
    });

    // Initial load
    fetchTerms();
  </script>
