            ${coCount ? `<div class="flex justify-between"><span>å…±ç¾æ¬¡æ•¸:</span><span class="font-mono">${coCount}</span></div>` : ''}
          </div>
          <div class="absolute inset-0 bg-white/0 group-hover:bg-white/10 rounded-xl transition-all duration-300"></div>
        </div>`;
      });
      $('rows-assoc').innerHTML = cards.join('');
      setStatus('status-assoc', `å®Œæˆï¼ˆå…± ${arr.length} å€‹é—œè¯è©å½™ï¼‰`);
    } else {
      const text = await resp.text();
